import React from "react";
import { formatDistanceToNow } from "date-fns";
import "./task.css";

class Task extends React.Component {
  onInput(id) {
    const currEl = document.getElementById(id);
    currEl.classList.toggle("completed");
  }
  buttonEdit(id) {
    const currEl = document.getElementById(id);
    currEl.classList.toggle("editing");
  }
  render() {
    const { todos, onDelete, timer } = this.props;

    const elements = todos.map((item) => {
      const { id, discription } = item;
      return (
        <li key={id} id={id}>
          <div className="view">
            <input
              className="toggle"
              type="checkbox"
              onClick={() => {
                this.onInput(id);
              }}
            />
            <label>
              <span className="description">{discription}</span>
              <span className="created">created {timer}</span>
            </label>
            <button
              className="icon icon-edit"
              onClick={() => {
                this.buttonEdit(id);
              }}
            ></button>
            <button
              className="icon icon-destroy"
              onClick={() => onDelete(id)}
            ></button>
          </div>
          <input
            type="text"
            className="edit"
            placeholder="Editing task"
          ></input>
        </li>
      );
    });

    return elements;
  }
}
export default Task;
