import React from "react";
import "./task.css";

class Task extends React.Component {
  state = {
    complete: false,
  };
  completeItem = () => {
    this.setState(({ complete }) => {
      return {
        complete: !complete,
      };
    });
  };
  render() {
    const { complete } = this.state;

    const { timer, discription, completed } = this.props;
    return (
      <div className="view">
        <input className="toggle" type="checkbox" onClick={completed} />
        <label>
          <span className="description">{discription}</span>
          <span className="created">created {timer}</span>
        </label>
        <button className="icon icon-edit"></button>
        <button className="icon icon-destroy"></button>
      </div>
    );
  }
}
export default Task;
