import React from "react";
import "./task.css";

class Task extends React.Component {
  state = {
    complete: false,
  };
  completeItem = () => {
    this.setState(({ complete }) => {
      return {
        complete: !complete,
      };
    });
  };
  render() {
    const { timer, discription, onDelete, id } = this.props;
    const { complete } = this.state;
    let classNames = "view";
    if (complete) {
      classNames = "completed";
    }
    return (
      <div className={classNames}>
        <input className="toggle" type="checkbox" onClick={this.completeItem} />
        <label>
          <span className="description">{discription}</span>
          <span className="created">created {timer}</span>
        </label>
        <button className="icon icon-edit"></button>
        <button
          className="icon icon-destroy"
          onClick={(id) => onDelete(id)}
        ></button>
      </div>
    );
  }
}
export default Task;
