import React from "react";
import { formatDistanceToNow } from "date-fns";
import "./task.css";
class Task extends React.Component {
  constructor() {
    super();
    this.date = new Date();
    this.state = {
      time: formatDistanceToNow(this.date, {
        includeSeconds: true,
      }),
      status: false,
    };
  }
  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      time: formatDistanceToNow(this.date, {
        includeSeconds: true,
      }),
    });
  }
  onInput = () => {
    this.setState({});
  };
  render() {
    const { todos } = this.props;
    const { status } = this.state;
    let className = "view";
    if (status) {
      className = "completed";
    }
    const elements = todos.map((item) => {
      const { id, discription } = item;
      // const style = {
      //   display: edit ? "block" : "none",
      // };
      return (
        <li id={id} className={className}>
          <div className="view">
            <input className="toggle" type="checkbox" onClick={this.onInput} />
            <label>
              <span className="description">{discription}</span>
              <span className="created">created {this.state.time}</span>
            </label>
            <button className="icon icon-edit"></button>
            <button className="icon icon-destroy"></button>
          </div>
          <input
            type="text"
            className="edit"
            placeholder="Editing task"
            // style={style}
          ></input>
        </li>
      );
    });

    return elements;
  }
}
export default Task;
