/* eslint-disable prettier/prettier */
import React from 'react'
import { v4 as uuidv4 } from 'uuid'

import Header from '../Header'
import TaskList from '../Task-list'
import Footer from '../Footer'

import './app.css'

export default class ToDoApp extends React.Component {
  state = {
    todoData: [this.createItem('Completed task'), this.createItem('Editing task'), this.createItem('Active task')],
  }
  addItem = (text) => {
    this.setState(({ todoData }) => {
      return {
        todoData: [...todoData, this.createItem(text)],
      }
    })
  }
  createItem(text) {
    return {
      discription: text,
      id: uuidv4(),
      complete: false,
      editing: false,
    }
  }
  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      return {
        todoData: todoData.filter((el) => el.id !== id),
      }
    })
  }
  onToggleComplete = (id) => {
    this.setState(({ todoData }) => {
      const currId = todoData.findIndex((el) => el.id === id)

      const currEl = todoData[currId]
      const newEl = { ...currEl, complete: !currEl.complete }

      const newArr = [...todoData.slice(0, currId), newEl, ...todoData.slice(currId + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  filterActive = () => {
    this.setState(({ todoData }) => {
      const newArr = []
      todoData.forEach((el) => {
        if (el.complete === true) {
          let item = { ...el, filterComplete: true }
          newArr.push(item)
        } else {
          let item = { ...el, filterComplete: false }
          newArr.push(item)
        }
      })
      return {
        todoData: newArr,
      }
    })
  }
  filterComplete = () => {
    this.setState(({ todoData }) => {
      const newArr = []
      todoData.forEach((el) => {
        if (el.complete === false) {
          let item = { ...el, filterComplete: true }
          newArr.push(item)
        } else {
          let item = { ...el, filterComplete: false }
          newArr.push(item)
        }
      })
      return {
        todoData: newArr,
      }
    })
  }
  filterShowAll = () => {
    this.setState(({ todoData }) => {
      const newArr = []
      todoData.forEach((el) => {
        if (el.filterComplete === true) {
          let item = { ...el, filterComplete: false }
          newArr.push(item)
        } else {
          newArr.push(el)
        }
      })
      return {
        todoData: newArr,
      }
    })
  }
  filter( filter) {
    switch (filter) {
    case 'all':
      return this.todoData
    case 'active':
      return this.todoData.filter((el) => !el.complete)
    case 'completed':
      return this.todoData.filter((el) => el.complete)
    default:
      return this.todoData
    }
  }
  clearCompleted = () => {
    this.setState(({ todoData }) => {
      const newArr = []
      todoData.forEach((el) => {
        if (el.complete !== true) {
          newArr.push(el)
        }
      })
      return {
        todoData: newArr,
      }
    })
  }
  editTask = (id) => {
    this.setState(({ todoData }) => {
      const currId = todoData.findIndex((el) => el.id === id)

      const currEl = todoData[currId]
      const newEl = { ...currEl, editing: !currEl.editing }

      const newArr = [...todoData.slice(0, currId), newEl, ...todoData.slice(currId + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  setNewDiscription = (e, id) => {
    this.setState(({ todoData }) => {
      const currId = todoData.findIndex((el) => el.id === id)
      const currEl = todoData[currId]
      const newEl = { ...currEl, discription: e.target.value }
      const newArr = [...todoData.slice(0, currId), newEl, ...todoData.slice(currId + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  onSubmit = (e, id) => {
    e.preventDefault()
    this.setState(({ todoData }) => {
      const currId = todoData.findIndex((el) => el.id === id)
      const currEl = todoData[currId]
      const newEl = { ...currEl, editing: !currEl.editing }
      const newArr = [...todoData.slice(0, currId), newEl, ...todoData.slice(currId + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  render() {
    const { todoData } = this.state
    const completeCount = todoData.filter((el) => !el.complete).length
    return (
      <section className="todoapp">
        <Header addItem={this.addItem} />
        <section className="main">
          <TaskList
            todos={todoData}
            onDeleted={this.deleteItem}
            completeItem={this.onToggleComplete}
            editTask={this.editTask}
            setNewDiscription={this.setNewDiscription}
            onSubmit={this.onSubmit}
          />
        </section>
        <Footer
          completeCount={completeCount}
          filterActive={this.filterActive}
          filterShowAll={this.filterShowAll}
          filterShowCompleted={this.filterComplete}
          filter={this.filter}
          clearCompleted={this.clearCompleted}
        />
      </section>
    )
  }
}
