import React from "react";
import "./task.css";

class Task extends React.Component {
  buttonEdit(id) {
    const currEl = document.getElementById(id);
    currEl.classList.toggle("editing");
  }
  render() {
    const { timer, discription } = this.props;
    return (
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          // onClick={() => complite(id)}
        />
        <label>
          <span className="description">{discription}</span>
          <span className="created">created {timer}</span>
        </label>
        <button
          className="icon icon-edit"
          // onClick={() => {
          //   this.buttonEdit(id);
          // }}
        ></button>
        <button className="icon icon-destroy"></button>
      </div>
    );
  }
}
export default Task;
