import { createRoot } from "react-dom/client";

import Header from "./components/header";
import TaskList from "./components/task-list";
import Footer from "./components/footer";
import "./index.css";
import React from "react";

class ToDoApp extends React.Component {
  unicId = 1;

  state = {
    todoData: [
      this.createItem("Completed task"),
      this.createItem("Editing task"),
      this.createItem("Active task"),
    ],
  };
  // addItem(text) {
  //   const newItem = {
  //     discription: text,
  //     id: this.unicId++,
  //   };
  //   this.setState(({ todoData }) => {
  //     const newArr = [...todoData, newItem];
  //     return {
  //       todoData: newArr,
  //     };
  //   });
  // }
  createItem(text) {
    return {
      discription: text,
      id: this.unicId++,
    };
  }

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const newData = todoData.filter((el) => el.id !== id);
      return {
        todoData: newData,
      };
    });
  };
  render() {
    return (
      <section className="todoapp">
        <Header addItem={this.createItem} />
        <section className="main">
          <TaskList todos={this.state.todoData} onDeleted={this.deleteItem} />
        </section>
        <Footer />
      </section>
    );
  }
}

const section = document.getElementById("root");
const root = createRoot(section);
root.render(<ToDoApp />);
