import React from "react";
import Task from "../task";
import "./task-list.css";
export default class TaskList extends React.Component {
  state = {
    complite: false,
  };
  completeItem = () => {
    this.setState(({ complite }) => {
      return {
        complite: !complite,
      };
    });
  };
  render() {
    const classname = "";
    if (complete) {
      classname = "completed";
    }
    const { todos, onDeleted, timer, complete } = this.props;
    const elements = todos.map((item) => {
      const { id, discription } = item;
      return (
        <li key={id} id={id} className={classname}>
          <Task
            timer={timer}
            discription={discription}
            completed={this.completeItem}
            // onDelete={(id) => onDeleted(id)}
            // complite={(id) => complite(id)}
          />
        </li>
      );
    });
    return <ul className="todo-list">{elements}</ul>;
  }
}
