import React from 'react'
import PropTypes from 'prop-types'
import { formatDistanceToNow } from 'date-fns'
import './task.css'

class Task extends React.Component {
  date = new Date()
  state = {
    time: formatDistanceToNow(this.date, {
      includeSeconds: true,
    }),
    editText: this.props.discription,
  }
  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000)
  }
  componentWillUnmount() {
    clearInterval(this.timerID)
  }
  tick() {
    this.setState({
      time: formatDistanceToNow(this.date, {
        includeSeconds: true,
      }),
    })
  }
  setNewDiscription = (e) => {
    this.setState(() => {
      return {
        editText: e.target.value,
      }
    })
  }
  render() {
    const { discription, onDelete, completeItem, editTask, complete, onSubmit, id, editing } = this.props
    const { edit } = this.state
    if (editing) {
      return (
        <form className="form-edit" onSubmit={() => onSubmit(edit, id)}>
          <input type="text" className="edit" value={discription} onChange={(e) => this.setNewDiscription(e)}></input>
        </form>
      )
    } else {
      return (
        <div className="view">
          <input className="toggle" type="checkbox" onChange={completeItem} checked={complete} />
          <label onClick={completeItem}>
            <span className="description">{discription}</span>
            <span className="created">created {this.state.time}</span>
          </label>
          <button className="icon icon-edit" onClick={editTask}></button>
          <button className="icon icon-destroy" onClick={onDelete}></button>
        </div>
      )
    }
  }
  static defaultProps = {
    completeItem: () => {},
  }
  static propTypes = {
    completeItem: PropTypes.func,
  }
}
export default Task
