import { createRoot } from "react-dom/client";
import { formatDistanceToNow } from "date-fns";
import Header from "./components/header";
import TaskList from "./components/task-list";
import Footer from "./components/footer";
import "./index.css";
import React from "react";

class ToDoApp extends React.Component {
  unicId = 1;
  date = new Date();
  state = {
    todoData: [
      this.createItem("Completed task"),
      this.createItem("Editing task"),
      this.createItem("Active task"),
    ],
    time: formatDistanceToNow(this.date, {
      includeSeconds: true,
    }),
  };

  addItem = (text) => {
    const newItem = this.createItem(text);
    this.setState(({ todoData }) => {
      const newArr = [...todoData, newItem];
      return {
        todoData: newArr,
      };
    });
  };
  createItem(text) {
    return {
      discription: text,
      id: this.unicId++,
      time: state.time,
    };
  }

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const newData = todoData.filter((el) => el.id !== id);
      return {
        todoData: newData,
      };
    });
  };
  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      time: formatDistanceToNow(this.date, {
        includeSeconds: true,
      }),
    });
  }
  render() {
    return (
      <section className="todoapp">
        <Header addItem={this.addItem} />
        <section className="main">
          <TaskList todos={this.state.todoData} onDeleted={this.deleteItem} />
        </section>
        <Footer />
      </section>
    );
  }
}

const section = document.getElementById("root");
const root = createRoot(section);
root.render(<ToDoApp />);
